CREATE INDEX "IX_Cities_Lon" ON "Cities" ("Lon");

CREATE INDEX "IX_Cities_Name" ON "Cities" ("Name");

CREATE INDEX "IX_Countries_ISO2" ON "Countries" ("ISO2");

CREATE INDEX "IX_Countries_ISO3" ON "Countries" ("ISO3");

CREATE INDEX "IX_Countries_Name" ON "Countries" ("Name");

